@LeetCode(id = 392, name = "Is Subsequence", url = "https://leetcode.com/problems/is-subsequence/")
public class IsSubsequence
{
	public boolean isSubsequence(String s, String t)
	{
		if (s.isEmpty())
		{
			return true;
		}

		int sIndex = 0;
		int tIndex = 0;
		while (tIndex < t.length())
		{
			if (s.charAt(sIndex) == t.charAt(tIndex))
			{
				sIndex++;
				if (sIndex == s.length())
				{
					return true;
				}
			}
			tIndex++;
		}

		return false;
	}

	public static void main(String[] args)
	{
		System.out.println(new IsSubsequence().isSubsequence("leeeeetcode", ""));
		System.out.println(new IsSubsequence().isSubsequence("axc", "ahbgdc"));
		System.out.println(new IsSubsequence().isSubsequence("abc", "ahbgdc"));
	}
}
